def get_keyword(ciphertext, key_len):
	# print(f"text:\n{ciphertext}")
	# print(f"key length:\n{key_len}")
	number_keys_used = len(ciphertext) / key_len
	print(f"number_keys_used: {number_keys_used}")
	let_nums = {v: k for k, v in dict(list(enumerate([i for i in 'ABCDEFGHIJKLMNOPQRSTUVWXYZ']))).items()}
	cipher_letters = []
	count = 0
	result = ""
	for i in range(len(ciphertext)):
		if count != key_len:
			count += 1
			result += ciphertext[i]
		else:
			cipher_letters.append(result)
			count = 0
			result = ""
			length = key_len * number_keys_used - len(ciphertext)
			cipheri_end = len(ciphertext[i:])
			length_ = cipheri_end / key_len
			if length == 0 and length_ < 1:
				cipher_letters.append(ciphertext[i:])
		len_ = len(ciphertext[i:])
		if len(ciphertext[i:]) < key_len:
			break

	not_equal = [i for i in cipher_letters if len(i) != key_len]

	return cipher_letters, not_equal


